/******************************************************************************
  ICON BUTTON COMPONENT
******************************************************************************/
// ----------------------------------------
// MODULE STATE & ID
var count = 0
const [cwd, dir] = [process.cwd(), __filename].map(x => new URL(x, 'file://').href)
const ID = dir.slice(cwd.length)
const STATE = { ids: {}, net: {} } // all state of component module
// ----------------------------------------
const sheet = new CSSStyleSheet
sheet.replaceSync(get_theme())
const default_opts = { }
const shopts = { mode: 'closed' }
// ----------------------------------------
module.exports = icon_button
// ----------------------------------------
function icon_button (opts = default_opts, protocol) {
  // ----------------------------------------
  // ID + JSON STATE
  // ----------------------------------------
  const id = `${ID}:${count++}` // assigns their own name
  const status = {}
  const state = STATE.ids[id] = { id, status, wait: {}, net: {}, aka: {} } // all state of component instance
  const cache = resources({})
  // ----------------------------------------
  // OPTS
  // ----------------------------------------
  const { src = '', src_active = '', activate = true, link = '', inverse = false } = opts
  const $src = src // @TODO: make those subscribable signals
  const $src_acitve = src_active
  // ----------------------------------------
  // PROTOCOL
  // ----------------------------------------
  const on = { 'activate': onactivate, 'deactivate': ondeactivate }
  const channel = use_protocol('up')({ protocol, state,  on })
  // ----------------------------------------
  // TEMPLATE
  // ----------------------------------------
  const el = document.createElement('div')
  const shadow = el.attachShadow(shopts)
  shadow.adoptedStyleSheets = [sheet]

  let icon_button
  if (link) {
    shadow.innerHTML = `<div class="icon_btn ${inverse && 'inverse'}">
      <a target="_blank" href=${link}></a>
    </div>`
    icon_button = shadow.querySelector('.icon_btn a')
  } else {
    shadow.innerHTML = `<div class="icon_btn ${inverse && 'inverse'}"> </div>`
    icon_button = shadow.querySelector('.icon_btn')
  }
  // ----------------------------------------
  // ELEMENTS
  // ----------------------------------------
  const [svg_icon, svg_active] = Object.assign(document.createElement('div'), {
    innerHTML: `${src} ${src_active}` // svg icons
  }).children
  icon_button.append(svg_icon)
  // Toggle Icon
  icon_button.onclick = onclick
  // ----------------------------------------
  // INIT
  // ----------------------------------------

  return el

  function ondeactivate () {
    state.status.active = false
    icon_button.classList.toggle('active', state.status.active)
    if (svg_active) icon_button.replaceChildren(svg_icon)
  }
  function onactivate () {
    if(activate){
      state.status.active = true
      icon_button.classList.toggle('active', state.status.active)
      if (svg_active) icon_button.replaceChildren(svg_active)
    }
  }
  function onclick (e) {
    channel.send({
      head: [id, channel.send.id, channel.mid++],
      type: 'click'
    })
  }
}
function get_theme () {
  return `
    .icon_btn {
      display: flex;
      justify-content: center;
      align-items: center;
      height: 40px;
      box-sizing: border-box;
      aspect-ratio: 1/1;
      cursor: pointer;
      border: 1px solid var(--primary_color);
      background-color: var(--bg_color);
    }
    .icon_btn svg {
      height: 25px;
      width: 25px;
      fill: var(--primary_color);
      pointer-events: none;
    }
    .icon_btn.inverse{
      background-color: var(--primary_color);
    }
    .icon_btn.inverse svg {
      fill: var(--bg_color);
    }
    .icon_btn *{
      fill: var(--primary_color);
    }
    .icon_btn.inverse *{
      fill: var(--bg_color);
    }
    .icon_btn.active {
      background-color: var(--ac-2)
    }
    .icon_btn.active *{
      fill: var(--dark);
    }
  `
}
// ----------------------------------------------------------------------------
function shadowfy (props = {}, sheets = []) {
  return element => {
    const el = Object.assign(document.createElement('div'), { ...props })
    const sh = el.attachShadow(shopts)
    sh.adoptedStyleSheets = sheets
    sh.append(element)
    return el
  }
}
function use_protocol (petname) {
  return ({ protocol, state, on = { } }) => {
    if (petname in state.aka) throw new Error('petname already initialized')
    const { id } = state
    const invalid = on[''] || (message => console.error('invalid type', message))
    if (protocol) return handshake(protocol(Object.assign(listen, { id })))
    else return handshake
    // ----------------------------------------
    // @TODO: how to disconnect channel
    // ----------------------------------------
    function handshake (send) {
      state.aka[petname] = send.id
      const channel = state.net[send.id] = { petname, mid: 0, send, on }
      return protocol ? channel : Object.assign(listen, { id })
    }
    function listen (message) {
      const [from] = message.head
      const by = state.aka[petname]
      if (from !== by) return invalid(message) // @TODO: maybe forward
      console.log(`[${id}]:${petname}>`, message)
      const { on } = state.net[by]
      const action = on[message.type] || invalid
      action(message)
    }
  }
}
// ----------------------------------------------------------------------------
function resources (pool) {
  var num = 0
  return factory => {
    const prefix = num++
    const get = name => {
      const id = prefix + name
      if (pool[id]) return pool[id]
      const type = factory[name]
      return pool[id] = type()
    }
    return Object.assign(get, factory)
  }
}